		    $output_file = 'qr/' . $account->account_key .'_'. $branch->name .'_'.  $invoice->invoice_number . '.jpg';

		    $inputqr = imagecreatefrompng($input_file);
		    list($width, $height) = getimagesize($input_file);
		    $output = imagecreatetruecolor($width, $height);
